drop table if exists personas CASCADE 
drop table if exists planetas CASCADE 

create table personas (id integer generated by default as identity, cantidad_visitas integer, estatura_en_metros float, fecha_nacimiento date not null, genero varchar(1) not null, nombre varchar(255) not null, numero_identificacion integer not null, peso_en_kilogramos float, planeta_id integer, primary key (id))
create table planetas (id integer generated by default as identity, cantidad_visitas integer, clima varchar(255), diametro integer not null, nombre varchar(255) not null, periodo_rotacion float not null, terreno varchar(255) not null, primary key (id))

alter table personas add constraint UK_personas unique (numero_identificacion)
alter table planetas add constraint UK_planetas unique (nombre)
alter table personas add constraint FK_personas foreign key (planeta_id) references planetas


